p.script:
var editor= $jq( '#mayak_product_creator_test' )

var result= true
$mayak_product_onSave.listen( editor[0], function( event ){
    event.catched( true )
    result= false
})

editor.find( '[mayak_button="success"]' ).click()

test.ok( result )

var editor= $jq( '#mayak_product_editor_test' )

